#include "ns3/core-module.h"#include "ns3/network-module.h"#include "ns3/csma-module.h"#include "ns3/internet-module.h"#include "ns3/point-to-point-module.h"#include "ns3/applications-module.h"#include "ns3/ipv4-global-routing-helper.h"#include "ns3/netanim-module.h"using namespace ns3;NS_LOG_COMPONENT_DEFINE("SecondScriptExample");int main(int argc, char *argv[]){  uint32_t nCsma = 3;    LogComponentEnable("UdpEchoClientApplication",LOG_LEVEL_INFO);  LogComponentEnable("UdpEchoServerApplication",LOG_LEVEL_INFO);  // Create nodes  NodeContainer p2pNodes;  p2pNodes.Create(2);  NodeContainer csmaNodes;  csmaNodes.Add(p2pNodes.Get(1));  csmaNodes.Create(nCsma);  // Set up point-to-point link  PointToPointHelper pointToPoint;  pointToPoint.SetDeviceAttribute("DataRate", StringValue("5Mbps"));  pointToPoint.SetChannelAttribute("Delay", StringValue("2ms"));  NetDeviceContainer p2pDevices;  p2pDevices = pointToPoint.Install(p2pNodes);  // Set up CSMA LAN  CsmaHelper csma;  csma.SetChannelAttribute("DataRate", StringValue("100Mbps"));  csma.SetChannelAttribute("Delay", TimeValue(NanoSeconds(6560)));  NetDeviceContainer csmaDevices;  csmaDevices = csma.Install(csmaNodes);  // Install internet stack  InternetStackHelper stack;  stack.Install(p2pNodes.Get(0));  stack.Install(csmaNodes);  // Assign IP addresses  Ipv4AddressHelper address;  address.SetBase("10.1.1.0", "255.255.255.0");  Ipv4InterfaceContainer p2pInterfaces;  p2pInterfaces = address.Assign(p2pDevices);  address.SetBase("10.1.2.0", "255.255.255.0");  Ipv4InterfaceContainer csmaInterfaces;  csmaInterfaces = address.Assign(csmaDevices);  // Set up UDP echo server  UdpEchoServerHelper echoServer(9);  ApplicationContainer serverApps = echoServer.Install(csmaNodes.Get(nCsma));  serverApps.Start(Seconds(1.0));  serverApps.Stop(Seconds(10.0));  // Set up UDP echo client  UdpEchoClientHelper echoClient(csmaInterfaces.GetAddress(nCsma), 9);  echoClient.SetAttribute("MaxPackets", UintegerValue(3));  echoClient.SetAttribute("Interval", TimeValue(Seconds(1.0)));  echoClient.SetAttribute("PacketSize", UintegerValue(1024));  ApplicationContainer clientApps = echoClient.Install(p2pNodes.Get(0));  clientApps.Start(Seconds(2.0));  clientApps.Stop(Seconds(10.0));  // Populate routing tables  Ipv4GlobalRoutingHelper::PopulateRoutingTables();  // Enable PCAP tracing  pointToPoint.EnablePcapAll("p2p");  // Enable PCAP for CSMA devices safely  for (uint32_t i = 0; i < csmaDevices.GetN() && i <= nCsma; ++i) {    std::ostringstream oss;    oss << "csma-" << i;    csma.EnablePcap(oss.str(), csmaDevices.Get(i), true);  }  // Set up NetAnim  AnimationInterface anim("p3.xml");  anim.SetConstantPosition(p2pNodes.Get(0), 10.0, 10.0);  // Set positions for csmaNodes dynamically  for (uint32_t i = 0; i < csmaNodes.GetN(); ++i) {    anim.SetConstantPosition(csmaNodes.Get(i), 20.0 + i * 10.0, 20.0 + i * 10.0);  }  // Schedule simulation stop  Simulator::Stop(Seconds(10.0));  // Run simulation  Simulator::Run();  Simulator::Destroy();  return 0;}